import { Injectable } from '@nestjs/common';
import * as moment from 'moment';
import puppeteer from 'puppeteer';
import { BookingsService } from 'src/bookings/bookings.service';
import { MPESAService } from 'src/payment/mpesa/mpesa.service';
import * as crypto from 'crypto';

@Injectable()
export class ReportsService {
  constructor(
    private bookingService: BookingsService,
    private paymentService: MPESAService
    )
    {}

  async generateReport(userId) {
    const browser = await puppeteer.launch({ headless: 'new' });
    const page = await browser.newPage();
    const bookings: any = await this.bookingService.getUserBookings(userId);
    const payments: any = await this.paymentService.getUserTransactions();

    const htmlContent = `
    <!DOCTYPE html>
    <html>
      <head>
        <style>
           body{
            font-family: Verdana, Geneva, Tahoma, sans-serif;
           }
          .styled-table {
            border-collapse: collapse;
            margin: 25px 0;
            font-size: 0.9em;
            font-family: sans-serif;
            min-width: 400px;
            box-shadow: 0 0 20px rgba(0, 0, 0, 0.15);
            width: 100%;
          }
          .styled-table thead {
            background-color: rgb(16, 143, 254);
            color: #ffffff;
            text-align: left;
          }
          .styled-table th,
          .styled-table td {
            padding: 12px 15px;
          }
          .styled-table tbody tr {
            border-bottom: 1px solid #dddddd;
          }
          .styled-table tbody tr:nth-of-type(even) {
            background-color: #f3f3f3;
          }
          .styled-table tbody tr.active-row {
            font-weight: bold;
          }
          footer {
            width: 800px;
            background-color: rgb(16, 143, 254);
            position: fixed;
            bottom: 0;
          }
          .line{
            border-bottom: solid 1px #000;
            width: 100%;
          }
          .info{
            display: grid;
            justify-content: end;
            font-size: 13px;
          }
          img{
            width: 100px;
          }
          .page-break {
            page-break-after: always;
          }
          .lead{
            font-weight: 300;
          }
        </style>
      </head>
      <body>
        <div class="info">
          <p>${moment(new Date()).format('Do MMMM YYYY')}</p>
          <p>XRA-${crypto.randomBytes(10).toString('hex')}</p>
        </div>
        <div>
         <img src="data:image/png;base64," alt="">
        </div>
    
      <div>
          <p class="lead">Bookings Overview</p>
          <div class="line"></div>
          <p>Total Bookings: <strong>${bookings.length}</strong> </p>
          <table class="styled-table">
          <thead>
            <tr>
              <th>Facility</th>
              <th>Dates Booked</th>
              <th>Total cost</th>
            </tr>
          </thead>
          <tbody>
          ${
            bookings.length > 0
              ? bookings.map(
                  (booking) => `
                  <tr>
                  <td>${booking.facility.name}</td>
                  <td>
                      <ul>
                      ${booking.dates.map(
                        (date) => `
                          <li>
                          ${moment(date.start).format(
                            'MMMM Do YYYY, h:mm:ss a'
                          )} - ${moment(date.end).format(
                          'MMMM Do YYYY, h:mm:ss a'
                        )}
                          </li>
                      `
                      )}
                      </ul>
                  </td>
                  <td><strong>${booking.subTotal}</strong></td>
                  </tr>
              `).join('')
              : `<tr><td colspan="4">No bookings available</td></tr>`
          }
          </tbody>
        </table>
    </div>

      <div>
          <p class="lead">Payments Overview</p>
          <div class="line"></div>
          <p>Total Payments: <strong>${payments.length}</strong> </p>
          <table class="styled-table">
          <thead>
            <tr>
              <th>Payment Number</th>
              <th>MPesa Receipt Number</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
          ${
            payments.length > 0
              ? payments.map(
                  (payment) => `
                  <tr>
                  <td>${payment.phoneNumber}</td>
                  <td>${payment.mpesaReceiptNumber? payment.mpesaReceiptNumber : '-'}</td>
                  <td>${moment(payment.createdAt).format('MMMM Do YYYY, h:mm:ss a')}</td>
                  </tr>
              `).join('')
              : `<tr><td colspan="4">No payments available</td></tr>`
          }
          </tbody>
        </table>
    </div>
        <footer>&nbsp;</footer>
      </body>
    </html>
        `;

    await page.setContent(htmlContent);
    const pdf = await page.pdf({
      printBackground: true,
      displayHeaderFooter: true,
    });

    await page.close();
    await browser.close();

    return pdf;
  }
}
